mapper.select:
select * from 	tb_auction_deposit
where  tb_auction_deposit.num_id = #{numId}
     and tb_auction_deposit.consumer_id=#{consumerId}

mapper.update:
update tb_auction_deposit t set
 t.status=#{status}
where
 t.num_id= #{numId}
 and t.consumer_id= #{consumerId}

update tb_auction_deposit t set
 t.status=#{status}
where
 t.id= #{id}
