mapper.select:
select

from tb_sku b left join tb_goods a on a.g_id = b.g_id
where b.sku_id = #{skuid}

mapper.update:
update tb_goods a set
    a.g_is_sale = '0'
where a.g_id = #{gId}

mapper.select:
select count(1) as num from tb_goods a where 1=1

  and a.g_id != #{gId}

and a.g_name = #{gName}
and SYSDATE() between a.g_start_time and a.g_end_time
