mapper.select:
select a.numPriceId, a.resource, case when a.net_type = '电信' then 1 when a.net_type = '联通' then 2 when a.net_type = '移动' then 3 else 0 end net_type,
a.province_name, a.city_name, a.feature, a.price, a.is_freeze,a.id, a.status, a.city_code,
a.with4,a.teleType,a.skuTobPrice,a.skuTocPrice,a.netType,a.numLevel,a.lowConsume,a.skuId,a.tags
from (
select b.rn, b.resource, b.id numPriceId, n.STATUS, b.city_code, b.net_type, b.province_name, b.city_name, b.feature, b.price, b.agent_id, n.is_freeze,
n.id id, n.with4, IF( n.tele_type IS NULL, '', n.tele_type ) AS teleType, sku.sku_tob_price AS skuTobPrice, sku.sku_toc_price AS skuTocPrice, n.net_type AS netType,
n.num_level AS numLevel, n.low_consume AS lowConsume, n.sku_id AS skuId, 'tag' AS tags
from (
SELECT  IF( @resource = b.resource, @row := @row + 1, @row := 1 ) as rn, @resource := b.resource AS resource,
b.id, b.city_code, b.net_type, b.province_name, b.city_name, b.feature, b.price, b.agent_id, b.num_id, b.sku_id
FROM tb_num_price b, ( SELECT @row := 0, @resource := NULL ) d
where b.is_del = 0
 and b.channel = #{param.channel}
 and (b.agent_id = -1 or b.agent_id = #{param.agentId})
 and b.province_code = #{param.provinceCode}
 and b.city_code = #{param.cityCode}
 and b.net_type = #{param.netType}
 and b.feature REGEXP ".*,"#{param.feature}",.*"
 and b.resource like #{param.temp}
 and b.resource like "%"#{param.resource}"%"
 and b.num_id = #{param.numId}
ORDER BY b.agent_id DESC
) b, tb_num n, tb_goods c, tb_sku sku
WHERE  b.num_id = n.id AND n.STATUS = 2 AND b.sku_id = sku.sku_id AND c.g_id = sku.g_id AND c.g_is_auc = 0 AND c.`status` = 1
AND sku.sku_goods_type = '4' AND c.g_is_sale = '1' AND SYSDATE( ) BETWEEN c.g_start_time AND c.g_end_time ) a where a.rn = 1


    order by
    case when a.province_name = '北京市' then 1 else 0 end desc,
    a.province_name desc,
    case when a.feature REGEXP ".*,AAAA,.*" THEN 2 when a.feature REGEXP ".*,AAA,.*" then 1 ELSE 0 end desc


    order by a.price desc


 limit #{param.start}, #{param.limit}

select count(*) FROM tb_num_price b, tb_num n, tb_goods c, tb_sku sku
where b.num_id = n.id and n.status = 2 and b.is_del = 0 and b.agent_id = -1 and b.sku_id = sku.sku_id
and c.g_id = sku.g_id and c.g_is_auc = 0 and c.`status`=1
and sku.sku_goods_type = '4'
and c.g_is_sale = '1'
and SYSDATE() between c.g_start_time and c.g_end_time

 and b.channel = #{param.channel}
 and b.province_code = #{param.provinceCode}
 and b.city_code = #{param.cityCode}
 and b.net_type = #{param.netType}
 and b.feature REGEXP ".*,"#{param.feature}",.*"
 and b.resource like #{param.temp}
 and b.resource like "%"#{param.resource}"%"
 and b.num_id = #{param.numId}

mapper.update:
update tb_num_price t, (
select n.id, c.ratio_price, ifnull(bp.price,n.base_price) price from tb_num_price n left join
(
select t.num_id, max(t.num) num, max(CAST(t.price AS SIGNED)) price from (
select r.num_id, r.num, case when locate('4',r.num) > 0 then d.ext2 else d.ext1 end price
from tb_num_rule r, tb_dict d where r.`value` = d.key_id and r.rule_type = 'feather_price' and d.key_group = 'feather_price' and d.is_del = 0
) t group by t.num_id
) bp on n.num_id = bp.num_id, tb_channel c where n.agent_id = -1 and n.channel = c.channel_id and c.is_del = 0 and n.is_del = 0
 and ((n.base_price != bp.price) or n.ratio_price != c.ratio_price)

) t1 set t.ratio_price = t1.ratio_price, t.base_price = t1.price, t.price = t1.ratio_price * t1.price where t.id = t1.id and t.is_del = 0

update tb_num_price p set p.feature = CONCAT(p.feature,#{feature}) where p.num_id in (

  #{item.numId}

) and p.is_del = 0

update tb_num_price np set np.is_del=1 where np.resource in (

  #{item}

) and np.channel=#{param.channel} and np.agent_id=#{param.agentId} and np.is_del=0

mapper.insert:
insert into tb_num_price(
sku_id, num_id,province_code,province_name,city_code,city_name,resource, base_price, net_type,feature,low_consume,corp_id,channel,ratio_price,price,agent_id,agent,is_del,add_date)
select np.sku_id, np.num_id,np.province_code,np.province_name,np.city_code,np.city_name,np.resource, np.base_price, np.net_type,np.feature,np.low_consume,np.corp_id,#{param.channel},np.ratio_price,#{param.price},#{param.agentId},np.agent,0,SYSDATE()
from tb_num_price np where np.agent_id=-1 and np.channel=#{param.channel} and np.resource in(

    #{item}

)
