mapper.update:
/*更新号码状态*/
update tb_num_price_agent npa join tb_num n on npa.num_id=n.id
set npa.status=n.status,npa.is_freeze=n.is_freeze where npa.status!=n.status or npa.is_freeze!=n.is_freeze;
