mapper.select:
select


from tb_num a
where a.sku_id = #{skuid}
and a.status in (${status})
limit 0,#{numcount}

select count(1) as num from tb_num a
where a.status = 1

    and a.num_resource = #{numResource}

select c.name, c.phone, a.id, a.num_resource, a.low_consume, b.sku_toc_price, b.sku_tob_price, b.sku_id
from tb_num a left join tb_sku b on b.sku_id = a.sku_id left join tb_goods d on d.g_id = b.g_id left join (select max(name) as name, max(phone) as phone, agent_city from tb_consumer GROUP BY agent_city) c on c.agent_city = a.city_id where b.sku_goods_type in (${skuGoodsType})
and a.city_id = #{agentCity}
and SYSDATE() between d.g_start_time and d.g_end_time and a.status = 2 and d.g_is_sale = '1' and d.g_is_auc = '0'

mapper.update:
update tb_num a set a.status = #{status} where a.id in (

    #{n.id}

)
