mapper.select:
select
from tb_order_item a where 1=1
and a.order_id = #{param.orderId}

select
b.sku_goods_type as skuGoodsType,

from tb_order_item a left join tb_sku b on b.sku_id = a.sku_id
where 1=1 and a.seller_id = #{sellerId}

    and a.order_id = #{orderId}

select b.sku_goods_type as skuGoodsType,
from tb_order_item a left join tb_sku b on b.sku_id = a.sku_id where 1=1
and a.order_id = #{orderId}
